(function ($) {
  "use strict";

  $('body').on('click', '.js-show-description', function (e) {
    e.preventDefault();
    var message = $(this).parent().find('input[type="hidden"]').val();
    var $modal = $('#contactMessage');
    $modal.find('.modal-body').html(message);
    $modal.modal('show');
  });
})(jQuery);
