(function ($) {
  "use strict";

  $('body').on('change', 'select[name="webinar_id"]', function (e) {
    var _this = this;

    e.preventDefault();
    var quizFilter = $('#quizFilter');
    $('#quizFilter option').each(function (key, element) {
      if ($(element).val() !== 'all') {
        $(element).addClass('d-none');
        quizFilter.prop('disabled', true);

        if ($(element).attr('data-webinar-id') === $(_this).val()) {
          $(element).removeClass('d-none');
          quizFilter.prop('disabled', false);
        }
      }
    });
  });
})(jQuery);
